    private void c2fConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_c2fConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempCel = (double)((Double.parseDouble(c2fField.getText())));
        double result = temp.c2f(tempCel);
        fLabel.setText(result + " Fahrenheit");
    }//GEN-LAST:event_c2fConvertActionPerformed

    private void c2kConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_c2kConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempCel = (double)((Double.parseDouble(c2kField.getText())));
        double result = temp.c2k(tempCel);
        kelLabel.setText(result + " Kelvin");
    }//GEN-LAST:event_c2kConvertActionPerformed

