    private void f2kConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_f2kConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempF = (double)((Double.parseDouble(f2kField.getText())));
        double result = temp.f2k(tempF);
        kelLabel2.setText(String.format("%.2f Kelvin", result));
    }//GEN-LAST:event_f2kConvertActionPerformed

    private void k2cConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_k2cConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempK = (double)((Double.parseDouble(k2cField.getText())));
        if(tempK >= 0){
            double result = temp.k2c(tempK);
            celLabel3.setText(result + " Celsius");
        }
        else{
            celLabel3.setText("Kelvin must be greater than 0");
        }
        
    }//GEN-LAST:event_k2cConvertActionPerformed

    private void k2fConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_k2fConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempk = (double)((Double.parseDouble(k2fField.getText())));
        if(tempk >= 0){
            double result = temp.k2f(tempk);
            fLabel4.setText(result + " Fahrenheit");
        }
        else{
            fLabel4.setText("Kelvin must be greater than 0");
        }
        
    }//GEN-LAST:event_k2fConvertActionPerformed

    private void f2cConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_f2cConvertActionPerformed
        Ex9Converter temp = new Ex9Converter();
        double tempF = (double)((Double.parseDouble(fah2celField.getText())));
        double result = temp.f2c(tempF);
        celLabel2.setText(String.format("%.2f Celsius", result));
    }//GEN-LAST:event_f2cConvertActionPerformed

